INSERT INTO "questions_locales" VALUES('How this portal is different from other similar ones for freelancers?','{"root":{"children":[{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"IT SKILL HOUSE is half automated portal. Which means that IT SKILL HOUSE is always up to date with projects and IT contractors'' data. We have colleagues who are always ready to update information and actively communicate with developers and customers, providing additional information if such is missing in the presented project or profile.","type":"text","version":1},{"type":"linebreak","version":1},{"type":"linebreak","version":1},{"detail":0,"format":0,"mode":"normal","style":"","text":"The portal is geographically limited and dedicated only for developers from Baltics, Poland and Ukraine.","type":"text","version":1},{"type":"linebreak","version":1},{"type":"linebreak","version":1},{"detail":0,"format":0,"mode":"normal","style":"","text":"IT SKILL HOUSE is in IT contracting area for over 11 years now. So we believe that by now we have pretty good idea what independent developers as well as customers which buy such services need.","type":"text","version":1},{"type":"linebreak","version":1},{"type":"linebreak","version":1},{"detail":0,"format":0,"mode":"normal","style":"","text":"We always listen to suggestions from our developers and customers. So this portal is live and constantly improving.","type":"text","version":1}],"direction":null,"format":"","indent":0,"type":"paragraph","version":1,"textFormat":0,"textStyle":""}],"direction":null,"format":"","indent":0,"type":"root","version":1}}',13,'en',4,NULL,NULL,NULL,NULL,1,1,NULL,NULL,NULL);
