INSERT INTO "questions_locales" VALUES('What IT SKILL HOUSE does?','{"root":{"children":[{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"IT SKILL HOUSE is an IT consulting company which operates in Lithuania since 2016. Company''s main goal is to help customers and independent developers (IT contractors) to find perfect match for ad-hoc efficient collaboration.","type":"text","version":1},{"type":"linebreak","version":1},{"type":"linebreak","version":1},{"detail":0,"format":0,"mode":"normal","style":"","text":"We help best IT contractors from Lithuania and neighbor countries to find interesting projects for temporary (hourly based) work with local and international companies and projects.","type":"text","version":1}],"direction":null,"format":"","indent":0,"type":"paragraph","version":1,"textFormat":0,"textStyle":""}],"direction":null,"format":"","indent":0,"type":"root","version":1}}',15,'en',6,NULL,NULL,NULL,NULL,1,1,NULL,NULL,NULL);
