INSERT INTO "privacy_page_locales" VALUES('"IT SKILL HOUSE" applicants personal data processing procedure','{"root":{"children":[{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"\"IT SKILL HOUSE\" applicants personal data processing procedure","type":"text","version":1}],"direction":null,"format":"","indent":0,"type":"heading","version":1,"tag":"h1"},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"General provisions","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"heading","version":1,"tag":"h2"},{"children":[{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The applicants'' personal data processing procedure (hereinafter referred to as the ","type":"text","version":1},{"detail":0,"format":2,"mode":"normal","style":"","text":"Procedure","type":"text","version":1},{"detail":0,"format":0,"mode":"normal","style":"","text":") defines how ","type":"text","version":1},{"detail":0,"format":1,"mode":"normal","style":"","text":"“IT SKILL HOUSE”","type":"text","version":1},{"detail":0,"format":0,"mode":"normal","style":"","text":" (hereinafter referred to as the ","type":"text","version":1},{"detail":0,"format":2,"mode":"normal","style":"","text":"Company","type":"text","version":1},{"detail":0,"format":0,"mode":"normal","style":"","text":") processes applicants’ personal data.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":1},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The Procedure has been prepared in compliance with Regulation (EU) 2016/679 (GDPR), the Law on the Legal Protection of Personal Data of the Republic of Lithuania, and other legal acts governing personal data protection and processing.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":2},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The purpose of this Procedure is to inform job applicants (at the Company) about the processing of their personal data during and after personnel selection, and to define requirements for the Company’s HR staff responsible for organizing and executing selection.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":3},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"This Procedure is published on the Company’s website, ","type":"text","version":1},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"itskillhouse.com","type":"text","version":1}],"direction":null,"format":"","indent":0,"type":"link","version":3,"fields":{"linkType":"custom","newTab":true,"url":"https://itskillhouse.com/"},"id":"69a00dabc497de1a0e8ff338"},{"detail":0,"format":0,"mode":"normal","style":"","text":", and in the Company’s public job advertisements.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":4}],"direction":null,"format":"","indent":0,"type":"list","version":1,"listType":"number","start":1,"tag":"ol"},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Processing of applicants’ personal data in selection procedures","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"heading","version":1,"tag":"h2"},{"children":[{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Applicants who submit personal data for participation in selection are provided information on data processing as specified in Article 4.2 of the Regulation. This is done by including links to the Company’s Privacy Policy and/or this Procedure in job advertisements and in the Registration section of the IT-HR system.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":1},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"If an applicant is recommended by a friend (an employee of the Company), that employee must first inform the applicant that their personal data (name, surname, email address, recommended position) will be provided to and processed by the Company under this Procedure. The employee must ensure the applicant has agreed to be recommended.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":2},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"For managing selection procedures, the Company uses its own IT-HR system (hereinafter referred to as the ","type":"text","version":1},{"detail":0,"format":2,"mode":"normal","style":"","text":"System","type":"text","version":1},{"detail":0,"format":0,"mode":"normal","style":"","text":"), managed by ","type":"text","version":1},{"detail":0,"format":1,"mode":"normal","style":"","text":"IT SKILL HOUSE JSC","type":"text","version":1},{"detail":0,"format":0,"mode":"normal","style":"","text":", registered in Lithuania. Applicants may sign up by registering directly, applying through a job ad, or following a link sent by the Company via email or LinkedIn message. To sign up, applicants must review the Privacy Policy and Terms of Use.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":3},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Personal data is processed based on applicant consent, expressed by sending a CV and/or other data via email or LinkedIn, agreeing to recommendation by a friend, or signing up in the System and submitting personal data there.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":4},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The Company must keep evidence that the applicant has been informed about processing of their personal data.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":5}],"direction":null,"format":"","indent":0,"type":"list","version":1,"listType":"number","start":1,"tag":"ol"},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Processing of applicants’ personal data after selection","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"heading","version":1,"tag":"h2"},{"children":[{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"CVs and/or other data of applicants who registered (or agreed to be registered) in the System, or participated in selection for a specific position, may be retained for ","type":"text","version":1},{"detail":0,"format":1,"mode":"normal","style":"","text":"3 years","type":"text","version":1},{"detail":0,"format":0,"mode":"normal","style":"","text":" after consent is given.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":1},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"If an applicant does not consent to retention, or requests retention to stop via email, their data must be destroyed (including paper data, mailbox content, and other storage media), except as provided in clause 3.5.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":2},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Data of applicants who submitted CVs and/or other data for possible future vacancies (not for a specific active selection) may also be retained for ","type":"text","version":1},{"detail":0,"format":1,"mode":"normal","style":"","text":"3 years","type":"text","version":1},{"detail":0,"format":0,"mode":"normal","style":"","text":", if consent is given.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":3},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The applicant may withdraw consent at any time by notifying the Company in writing via email and/or LinkedIn, rejecting consent in the System, or deleting their System account.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":4},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Based on legitimate interest, in individual cases (e.g., where a recruitment agency is entitled to payment for hires within a defined period), the Company may retain applicant data for ","type":"text","version":1},{"detail":0,"format":1,"mode":"normal","style":"","text":"1 year","type":"text","version":1},{"detail":0,"format":0,"mode":"normal","style":"","text":" after selection ends and process it for communication traceability.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":5}],"direction":null,"format":"","indent":0,"type":"list","version":1,"listType":"number","start":1,"tag":"ol"},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Categories of personal data, purposes, and sources","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"heading","version":1,"tag":"h2"},{"children":[{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The Company processes only personal data related to applicants’ qualifications, professional skills, and business qualities, except where law provides otherwise. Information on private life is not collected unless relevant to these criteria.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":1},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The Company may process:","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":2},{"children":[{"children":[{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Name, surname, email, phone number, expected position, education, professional experience, qualifications, CV and cover letter content, applicant comments.","type":"text","version":1}],"direction":null,"format":"","indent":1,"type":"listitem","version":1,"value":1},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Recommendations and previous employer responses, including sender identification, sender contact details, and content.","type":"text","version":1}],"direction":null,"format":"","indent":1,"type":"listitem","version":1,"value":2},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Applicant rating information: interviewer insights/opinions, test results, and responses.","type":"text","version":1}],"direction":null,"format":"","indent":1,"type":"listitem","version":1,"value":3}],"direction":null,"format":"","indent":0,"type":"list","version":1,"listType":"bullet","start":1,"tag":"ul"}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":3},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The Company does ","type":"text","version":1},{"detail":0,"format":1,"mode":"normal","style":"","text":"not","type":"text","version":1},{"detail":0,"format":0,"mode":"normal","style":"","text":" process special categories of personal data or data on convictions/criminal acts, except where legally required for statutory role requirements or other legal grounds.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":3},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Special categories (racial/ethnic origin, political opinions, religious/philosophical beliefs, trade union membership, genetic/biometric data, health data, sex life/sexual orientation) must not be processed unless allowed by law.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":4},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The Company does not check applicants’ Facebook, Twitter, or other social networks (except LinkedIn), since such data is generally unrelated to qualifications and professional suitability.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":5},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Data listed in clause 4.2 may be processed to:","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":6},{"children":[{"children":[{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Identify the applicant,","type":"text","version":1}],"direction":null,"format":"","indent":1,"type":"listitem","version":1,"value":1},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Communicate with the applicant,","type":"text","version":1}],"direction":null,"format":"","indent":1,"type":"listitem","version":1,"value":2},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Assess qualifications, experience, and suitability for a specific position.","type":"text","version":1}],"direction":null,"format":"","indent":1,"type":"listitem","version":1,"value":3}],"direction":null,"format":"","indent":0,"type":"list","version":1,"listType":"bullet","start":1,"tag":"ul"}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":7},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The Company may receive applicant data (CV, questionnaire, cover letter, etc.) directly from the applicant via email, System registration, and/or LinkedIn.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":7},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The Company may also obtain data from the System and from job search/selection/mediation providers (e.g., recruitment agencies, job portals, career social networks).","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":8},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The Company may collect qualification/professional data from the applicant’s current employer only after obtaining applicant consent (written, email, or other provable form).","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":9},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The Company may collect qualification/professional data from previous employers without consent, provided the applicant is informed about that source.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":10}],"direction":null,"format":"","indent":0,"type":"list","version":1,"listType":"number","start":1,"tag":"ol"},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Provision of applicant data to third parties","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"heading","version":1,"tag":"h2"},{"children":[{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The Company may share applicants’ general (publicly available) data with third parties (e.g., clients where the applicant may participate in service delivery).","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":1},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"In line with privacy legislation, the Company signs appropriate data processing/data transfer agreements with third-party providers, including standard contractual clauses for transfers outside the EEA where required.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":2},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The Company also signs data processing agreements with IT/server providers, system administrators, and other supporting service providers that process data under Company instructions.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":3},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Applicant data may be provided to competent authorities/law enforcement/supervisory bodies only under lawful request or where required by law to protect Company rights, customer/employee/resource safety, or to establish, file, or defend legal claims.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":4}],"direction":null,"format":"","indent":0,"type":"list","version":1,"listType":"number","start":1,"tag":"ol"},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Final provisions","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"heading","version":1,"tag":"h2"},{"children":[{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The Procedure is reviewed and, if needed, updated at least once per year. Review triggers include legal changes, practice changes, or changes in Company processing activities.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":1},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The Procedure and amendments are approved by the Company CEO.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":2},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"For questions about applicants’ personal data processing, applicants are encouraged to contact: ","type":"text","version":1},{"children":[{"detail":0,"format":1,"mode":"normal","style":"","text":"info@itskillhouse.com","type":"text","version":1}],"direction":null,"format":"","indent":0,"type":"link","version":3,"fields":{"linkType":"custom","newTab":true,"url":"mailto:info@itskillhouse.com"},"id":"69a00dabc497de1a0e8ff339"},{"detail":0,"format":0,"mode":"normal","style":"","text":".","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":3}],"direction":null,"format":"","indent":0,"type":"list","version":1,"listType":"number","start":1,"tag":"ol"}],"direction":null,"format":"","indent":0,"type":"root","version":1}}',1,'en',1,'"IT SKILL HOUSE" applicants personal data processing procedure | IT Skill House','info@itskillhouse.com In case of any questions related to processing the applicants'' personal data, applicants are recommended to contact the Company via…',3,NULL,1,1,NULL,NULL,NULL);
INSERT INTO "privacy_page_locales" VALUES('"IT SKILL HOUSE" applicants personal data processing procedure','{"root":{"children":[{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"\"IT SKILL HOUSE\" applicants personal data processing procedure","type":"text","version":1}],"direction":null,"format":"","indent":0,"type":"heading","version":1,"tag":"h1"},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"General provisions","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"heading","version":1,"tag":"h2"},{"children":[{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The applicants'' personal data processing procedure (hereinafter referred to as the ","type":"text","version":1},{"detail":0,"format":2,"mode":"normal","style":"","text":"Procedure","type":"text","version":1},{"detail":0,"format":0,"mode":"normal","style":"","text":") defines how ","type":"text","version":1},{"detail":0,"format":1,"mode":"normal","style":"","text":"“IT SKILL HOUSE”","type":"text","version":1},{"detail":0,"format":0,"mode":"normal","style":"","text":" (hereinafter referred to as the ","type":"text","version":1},{"detail":0,"format":2,"mode":"normal","style":"","text":"Company","type":"text","version":1},{"detail":0,"format":0,"mode":"normal","style":"","text":") processes applicants’ personal data.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":1},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The Procedure has been prepared in compliance with Regulation (EU) 2016/679 (GDPR), the Law on the Legal Protection of Personal Data of the Republic of Lithuania, and other legal acts governing personal data protection and processing.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":2},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The purpose of this Procedure is to inform job applicants (at the Company) about the processing of their personal data during and after personnel selection, and to define requirements for the Company’s HR staff responsible for organizing and executing selection.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":3},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"This Procedure is published on the Company’s website, ","type":"text","version":1},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"itskillhouse.com","type":"text","version":1}],"direction":null,"format":"","indent":0,"type":"link","version":3,"fields":{"linkType":"custom","newTab":true,"url":"https://itskillhouse.com/"},"id":"69a01033c497de1a0e8ff33a"},{"detail":0,"format":0,"mode":"normal","style":"","text":", and in the Company’s public job advertisements.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":4}],"direction":null,"format":"","indent":0,"type":"list","version":1,"listType":"number","start":1,"tag":"ol"},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Processing of applicants’ personal data in selection procedures","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"heading","version":1,"tag":"h2"},{"children":[{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Applicants who submit personal data for participation in selection are provided information on data processing as specified in Article 4.2 of the Regulation. This is done by including links to the Company’s Privacy Policy and/or this Procedure in job advertisements and in the Registration section of the IT-HR system.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":1},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"If an applicant is recommended by a friend (an employee of the Company), that employee must first inform the applicant that their personal data (name, surname, email address, recommended position) will be provided to and processed by the Company under this Procedure. The employee must ensure the applicant has agreed to be recommended.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":2},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"For managing selection procedures, the Company uses its own IT-HR system (hereinafter referred to as the ","type":"text","version":1},{"detail":0,"format":2,"mode":"normal","style":"","text":"System","type":"text","version":1},{"detail":0,"format":0,"mode":"normal","style":"","text":"), managed by ","type":"text","version":1},{"detail":0,"format":1,"mode":"normal","style":"","text":"IT SKILL HOUSE JSC","type":"text","version":1},{"detail":0,"format":0,"mode":"normal","style":"","text":", registered in Lithuania. Applicants may sign up by registering directly, applying through a job ad, or following a link sent by the Company via email or LinkedIn message. To sign up, applicants must review the Privacy Policy and Terms of Use.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":3},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Personal data is processed based on applicant consent, expressed by sending a CV and/or other data via email or LinkedIn, agreeing to recommendation by a friend, or signing up in the System and submitting personal data there.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":4},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The Company must keep evidence that the applicant has been informed about processing of their personal data.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":5}],"direction":null,"format":"","indent":0,"type":"list","version":1,"listType":"number","start":1,"tag":"ol"},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Processing of applicants’ personal data after selection","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"heading","version":1,"tag":"h2"},{"children":[{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"CVs and/or other data of applicants who registered (or agreed to be registered) in the System, or participated in selection for a specific position, may be retained for ","type":"text","version":1},{"detail":0,"format":1,"mode":"normal","style":"","text":"3 years","type":"text","version":1},{"detail":0,"format":0,"mode":"normal","style":"","text":" after consent is given.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":1},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"If an applicant does not consent to retention, or requests retention to stop via email, their data must be destroyed (including paper data, mailbox content, and other storage media), except as provided in clause 3.5.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":2},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Data of applicants who submitted CVs and/or other data for possible future vacancies (not for a specific active selection) may also be retained for ","type":"text","version":1},{"detail":0,"format":1,"mode":"normal","style":"","text":"3 years","type":"text","version":1},{"detail":0,"format":0,"mode":"normal","style":"","text":", if consent is given.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":3},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The applicant may withdraw consent at any time by notifying the Company in writing via email and/or LinkedIn, rejecting consent in the System, or deleting their System account.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":4},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Based on legitimate interest, in individual cases (e.g., where a recruitment agency is entitled to payment for hires within a defined period), the Company may retain applicant data for ","type":"text","version":1},{"detail":0,"format":1,"mode":"normal","style":"","text":"1 year","type":"text","version":1},{"detail":0,"format":0,"mode":"normal","style":"","text":" after selection ends and process it for communication traceability.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":5}],"direction":null,"format":"","indent":0,"type":"list","version":1,"listType":"number","start":1,"tag":"ol"},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Categories of personal data, purposes, and sources","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"heading","version":1,"tag":"h2"},{"children":[{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The Company processes only personal data related to applicants’ qualifications, professional skills, and business qualities, except where law provides otherwise. Information on private life is not collected unless relevant to these criteria.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":1},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The Company may process:","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":2},{"children":[{"children":[{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Name, surname, email, phone number, expected position, education, professional experience, qualifications, CV and cover letter content, applicant comments.","type":"text","version":1}],"direction":null,"format":"","indent":1,"type":"listitem","version":1,"value":1},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Recommendations and previous employer responses, including sender identification, sender contact details, and content.","type":"text","version":1}],"direction":null,"format":"","indent":1,"type":"listitem","version":1,"value":2},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Applicant rating information: interviewer insights/opinions, test results, and responses.","type":"text","version":1}],"direction":null,"format":"","indent":1,"type":"listitem","version":1,"value":3}],"direction":null,"format":"","indent":0,"type":"list","version":1,"listType":"bullet","start":1,"tag":"ul"}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":3},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The Company does ","type":"text","version":1},{"detail":0,"format":1,"mode":"normal","style":"","text":"not","type":"text","version":1},{"detail":0,"format":0,"mode":"normal","style":"","text":" process special categories of personal data or data on convictions/criminal acts, except where legally required for statutory role requirements or other legal grounds.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":3},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Special categories (racial/ethnic origin, political opinions, religious/philosophical beliefs, trade union membership, genetic/biometric data, health data, sex life/sexual orientation) must not be processed unless allowed by law.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":4},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The Company does not check applicants’ Facebook, Twitter, or other social networks (except LinkedIn), since such data is generally unrelated to qualifications and professional suitability.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":5},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Data listed in clause 4.2 may be processed to:","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":6},{"children":[{"children":[{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Identify the applicant,","type":"text","version":1}],"direction":null,"format":"","indent":1,"type":"listitem","version":1,"value":1},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Communicate with the applicant,","type":"text","version":1}],"direction":null,"format":"","indent":1,"type":"listitem","version":1,"value":2},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Assess qualifications, experience, and suitability for a specific position.","type":"text","version":1}],"direction":null,"format":"","indent":1,"type":"listitem","version":1,"value":3}],"direction":null,"format":"","indent":0,"type":"list","version":1,"listType":"bullet","start":1,"tag":"ul"}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":7},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The Company may receive applicant data (CV, questionnaire, cover letter, etc.) directly from the applicant via email, System registration, and/or LinkedIn.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":7},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The Company may also obtain data from the System and from job search/selection/mediation providers (e.g., recruitment agencies, job portals, career social networks).","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":8},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The Company may collect qualification/professional data from the applicant’s current employer only after obtaining applicant consent (written, email, or other provable form).","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":9},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The Company may collect qualification/professional data from previous employers without consent, provided the applicant is informed about that source.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":10}],"direction":null,"format":"","indent":0,"type":"list","version":1,"listType":"number","start":1,"tag":"ol"},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Provision of applicant data to third parties","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"heading","version":1,"tag":"h2"},{"children":[{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The Company may share applicants’ general (publicly available) data with third parties (e.g., clients where the applicant may participate in service delivery).","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":1},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"In line with privacy legislation, the Company signs appropriate data processing/data transfer agreements with third-party providers, including standard contractual clauses for transfers outside the EEA where required.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":2},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The Company also signs data processing agreements with IT/server providers, system administrators, and other supporting service providers that process data under Company instructions.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":3},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Applicant data may be provided to competent authorities/law enforcement/supervisory bodies only under lawful request or where required by law to protect Company rights, customer/employee/resource safety, or to establish, file, or defend legal claims.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":4}],"direction":null,"format":"","indent":0,"type":"list","version":1,"listType":"number","start":1,"tag":"ol"},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"Final provisions","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"heading","version":1,"tag":"h2"},{"children":[{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The Procedure is reviewed and, if needed, updated at least once per year. Review triggers include legal changes, practice changes, or changes in Company processing activities.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":1},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"The Procedure and amendments are approved by the Company CEO.","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":2},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"For questions about applicants’ personal data processing, applicants are encouraged to contact: ","type":"text","version":1},{"children":[{"detail":0,"format":1,"mode":"normal","style":"","text":"info@itskillhouse.com","type":"text","version":1}],"direction":null,"format":"","indent":0,"type":"link","version":3,"fields":{"linkType":"custom","newTab":true,"url":"mailto:info@itskillhouse.com"},"id":"69a01033c497de1a0e8ff33b"},{"detail":0,"format":0,"mode":"normal","style":"","text":".","type":"text","version":1}],"direction":null,"format":"start","indent":0,"type":"listitem","version":1,"value":3}],"direction":null,"format":"","indent":0,"type":"list","version":1,"listType":"number","start":1,"tag":"ol"}],"direction":null,"format":"","indent":0,"type":"root","version":1}}',2,'lt',1,'"IT SKILL HOUSE" applicants personal data processing procedure | IT Skill House','info@itskillhouse.com In case of any questions related to processing the applicants'' personal data, applicants are recommended to contact the Company via…',3,NULL,1,1,NULL,NULL,NULL);
